import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { Menu, X } from 'lucide-react';

const Navbar = () => {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const navLinks = [
    { title: 'Why Veyo', href: '#why-veyo' },
    { title: 'Features', href: '#features' },
    { title: 'Fleet Management', href: '#fleet' },
    { title: 'Security', href: '#security' },
    { title: 'Pricing', href: '#pricing' }
  ];

  return (
    <motion.nav
      initial={{ y: -100 }}
      animate={{ y: 0 }}
      className={`fixed w-full z-50 transition-all duration-300 ${isScrolled ? 'bg-primary/90 backdrop-blur-md' : 'bg-transparent'}`}
    >
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-20">
          <motion.div whileHover={{ scale: 1.05 }} className="flex-shrink-0 cursor-pointer">
            <span className="text-2xl font-bold bg-gradient-to-r from-secondary to-accent bg-clip-text text-transparent">VEYO</span>
          </motion.div>

          <div className="hidden md:flex items-center space-x-8">
            {navLinks.map(link => <motion.a key={link.title} href={link.href} whileHover={{ scale: 1.05 }} className="text-white/80 hover:text-white transition-colors">{link.title}</motion.a>)}
            <motion.button whileHover={{ scale: 1.05 }} className="px-6 py-2 rounded-full bg-gradient-to-r from-secondary to-accent text-white font-medium hover:shadow-glow transition-all">Get Started</motion.button>
          </div>

          <div className="md:hidden">
            <button onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)} className="text-white p-2">
              {isMobileMenuOpen ? <X size={24} /> : <Menu size={24} />}
            </button>
          </div>
        </div>

        {isMobileMenuOpen && (
          <div className="md:hidden px-2 pt-2 pb-3 space-y-1">
            {navLinks.map(link => <a key={link.title} href={link.href} className="block px-3 py-2 text-white/80 hover:text-white hover:bg-white/10 rounded-md">{link.title}</a>)}
            <button className="w-full mt-4 px-6 py-2 rounded-full bg-gradient-to-r from-secondary to-accent text-white font-medium hover:shadow-glow transition-all">Get Started</button>
          </div>
        )}
      </div>
    </motion.nav>
  );
};

export default Navbar;
